import react, {Component} from 'react';
import './App.css';
import Person from './components/Person';
import { Book } from './components/Book';


// Functional Component
function App() {
  state={
    books:[
      {bookName:"Ahkame Jindegi", writer:"Mo. Hemayet Uddin Saheb(dam.)"},
      {bookName:" Burhanul Kuran", writer:"Mo. Mahmudul Hasan saheb (Dam.)"},
      {bookName:" Fazayele Amol", writer:"Mo. Jakaiya saheb(Roh.)"},
    ]
  }

  return (
    <div className="App">
      <h1>Book List</h1>
      <Book bookName={this.state.books[0].bookName} writer={this.state.books[0].writer}/>
      <Book bookName={this.state.books[1].bookName} writer={this.state.books[1].writer}/>
      <Book bookName={this.state.books[2].bookName} writer={this.state.books[2].writer}/>
    </div>
  );
}

