    { name: "Setara", email: "Setara@gmail.com", contact:'111',address:[
      {thana:'Patukhali',district:'Patuakhali',country:'Bangladesh'}
    ]},
  ];

  return (
    <>
      <Container className="text-center">
        <h2> Reuse Component Array With List</h2>
        <h1>
          {
            students.map((data)=>
            data.name

            )
