import React, { Component } from 'react'

export default class App extends Component {
  constructor(){
    super();
    this.state={
      count:1
    }
  }
  render() {
    return (
      <>
        <h1>Pure Component In React  {this.state.count}</h1>
        <button onClick={()=>this.setState({count:this.state.count})} >Update Count</button>
      </>
    )
  }
}




