import React,{useState} from "react";

const App = () => {

 const [name, setName] = useState()
 const [inerest, setInerest] = useState()
 const [tnc, setTnc] = useState()




  const getFromData =(event)=>{
    console.log(name,tnc,inerest)
    event.preventDefault()
  }
  return (
    <>
      <div className="container">
        <h1> React Js Form</h1>
        <form onSubmit={getFromData}>
        <input type="text" placeholder="Enter Name" onChange={(e)=>setName(e.target.value)} /><br /><br />
        <select
          class="form-select form-select-lg mb-3"
          aria-label=".form-select-lg example" onChange={(e)=>setInerest(e.target.value)}
        >
          <option selected>select menu</option>
          <option value="1">One</option>
          <option value="2">Two</option>
          <option value="3">Three</option>
        </select>
        <div class="form-check">
  <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault"onChange={(e)=>setTnc(e.target.checked)}/>
  <label class="form-check-label" for="flexCheckDefault">
    Terms & Conditions
  </label>
</div>
<input type="submit" value="Submit" />
</form>

</div>
    </>
  );
};

export default App;

