import React, { useState, useEffect } from "react";

const App = () => {
  const [data, setData] = useState([]);
  const [name, setName] = useState();
  const [email, setEmail] = useState()
  const [contact, setContact] = useState()
  useEffect(() => {
    fetch("http://localhost:3333/list/").then((result) => {
      result.json().then((resp) => {
        // console.log(resp)
        setData(resp);
      });
    });
  }, []);
  console.log(data);
  return (
    <>
      <h2>Get Api Example</h2>
      <table class="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col">Contact</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.email}</td>
              <td>{item.contact}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div class="container">
      <h3>Add Api Data By Post Method</h3>
            <input type="text"name="name" value={name} placeholder="Name" id="" />
            <input type="text"name="email" value={email} placeholder="Email" id="" />
            <input type="text"name="contact" value={contact} placeholder="Mobile" id="" />
            <input type="button" value="Save Data" />

      </div>




    </>

  );
};

export default App;

