// Class Component
class App extends Component{

  state={
    books:[
      {bookName:"ahkame Jindegi", writer:" Mo. Hemayet Uddin"},
      {bookName:"Fajayele Amol", writer:" Mo. Jakaria saheb (Roh.)"},
      {bookName:"ahkame Jindegi", writer:" Mo. Hemayet Uddin"}
    ]
  }

  changeBookState = (newBookName) =>{
    this.setState(
      {
        books:[
          {bookName:newBookName, writer:" Mo. Hemayet Uddin"},
          {bookName:"Fajayele Amol", writer:" Mo. Jakaria saheb (Roh.)"},
          {bookName:"fazayele Sadakath", writer:" Mo. Hemayet Uddin"}
        ]
      }
    )
  }

  changeStateWithInput = (event)=>{
    this.setState(
      {
        books:[
          {bookName:event.target.value, writer:" Mo. Hemayet Uddin"},
          {bookName:"Fajayele Amol", writer:" Mo. Jakaria saheb (Roh.)"},
          {bookName:"fazayele Sadakath", writer:" Mo. Hemayet Uddin"}
        ]
      }
    )
  }




  render(){

    const bookState = this.state.books;
    console.log(bookState)


  return (
    <div className="App">
    <button onClick={()=>this.changeBookState('Burhanul Quran')}>Change State</button>
    <input type="text" onChange={this.changeStateWithInput}/>
      <h1>Book List</h1>
      <Book bookName={this.state.books[0].bookName} writer={this.state.books[0].writer}
      inputName={this.changeStateWithInput}/>
      <Book bookName={this.state.books[1].bookName} writer={this.state.books[1].writer}/>
      <Book bookName={this.state.books[2].bookName} writer={this.state.books[2].writer}
      change={this.changeBookState.bind(this,'ahkame jindegi')}/>

    </div>
  );
  }
}

export default App;
