import React, { Component } from "react";


export default class App extends Component {

constructor(){
super()
  this.state={
    count : 5
  }
}

  render() {
    return (
      <>
        <div>

            <h2> Count :{this.state.count} </h2>
            <button onClick={()=>(
              this.setState({count:this.state.count})
            )}>add</button>


        </div>
      </>
    );
  }
}
