import react, {Component} from 'react';
import './App.css';
import Person from './components/Person';
import { Book } from './components/Book';


// Functional Component
// function App() {
//   return (
//     <div className="App">
//       <h1>Book List</h1>
//       <Book></Book>
//     </div>
//   );
// }

// Class Component
class App extends Component{

  state={
    books:[
      {bookName:"ahkame Jindegi", writer:" Mo. Hemayet Uddin"},
      {bookName:"Fajayele Amol", writer:" Mo. Jakaria saheb (Roh.)"},

    ]
  }

  changeBookState = (newBookName) =>{
    this.setState(
      {
        books:[
          {bookName:newBookName, writer:" Mo. Hemayet Uddin"},
          {bookName:"Fajayele Amol", writer:" Mo. Jakaria saheb (Roh.)"},
          {bookName:"fazayele Sadakath", writer:" Mo. Hemayet Uddin"}
        ]
      }
    )
  }

  changeStateWithInput = (event)=>{
    this.setState(
      {
        books:[
          {bookName:event.target.value, writer:" Mo. Hemayet Uddin"},
          {bookName:"Fajayele Amol", writer:" Mo. Jakaria saheb (Roh.)"},
          {bookName:"fazayele Sadakath", writer:" Mo. Hemayet Uddin"}
        ]
      }
    )
  }




  render(){


    const books = this.state.books.map(book =>{
      return(
        <Book bookName={book.bookName} writer={book.writer} />
      )

    })
    // console.log(bookState)


  return (
    <div className="App">
    <button onClick={()=>this.changeBookState('Burhanul Quran')}>Change State</button>
      <input type="text" onChange={this.changeStateWithInput}/>
      <h1>Book List</h1>
      {books}
    </div>
  );
  }
}

export default App;
